import { judge } from "./utils"

/**
 * 大数相加
 */

const bigAdd = (a: string, b: string) => {
  const aArr = a.split('').reverse()
  const bArr = b.split('').reverse()

  const len = aArr.length > bArr.length ? aArr.length : bArr.length

  const results = [];
  let stepValue = 0;
  for(let i=0; i<len; i++) {
    const value = Number(aArr[i] || 0) + Number(bArr[i] || 0) + stepValue;
    const curValue = value % 10
    stepValue = Math.floor(value / 10)

    results.unshift(curValue)
  }

  return results.join('')

}

judge('63', bigAdd, '4', '59')
judge('98', bigAdd, '48', '50')
judge('0', bigAdd, '0', '0')
judge('90', bigAdd, '0', '90')
judge(
  '', 
  bigAdd, 
  '',
  ''
)

export {
  bigAdd
}